  return(
    <h1>{course}</h1>
  )
}
const Content = ({parts, exercises}) => {
  console.log(parts)
  return (
    <>
      {parts.forEach((element, ind) => {
        return <p>{parts[ind]} {exercises[ind]}</p>
      })}
    </>
  )
}

const Total = ({excercises}) => <p>Number of excercises {excercises.reduce((a, b) => a + b, 0)}</p>

