const Header = ({course}) => {

  return(
    <h1>{course}</h1>
  )
}

const Part = ({part, exercise}) => <p>{part} {exercise}</p>
const Content = ({parts}) => {

  return (
    <>
      <Part part={parts[0].name} exercise={parts[0].excercises}/>
      <Part part={parts[1].name} exercise={parts[1].excercises}/>
      <Part part={parts[2].name} exercise={parts[2].excercises}/>


    </>
  )
}

const Total = ({parts}) => <p>Number of excercises {parts[0].excercises + parts[1].excercises + parts[2].excercises}</p>

const App = () => {

  const course = {
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10
      },
      {
        name: 'Using props to pass data',
        exercises: 7
      },
      {
        name: 'State of a component',
        exercises: 14
      }
    ]
  }


  return (
    <div>
      <Header course={course.name} />
      <Content parts={course.parts} />
      <Total parts={course.parts} />
    </div>
  )
}

export default App
